workflow.description:
Workflow to add a specified set of Rest Operations to a specified NetScaler RESTHost. Done for consistency based on code and an idea from  http://www.vcoflow.co.uk/

Method_Netscaler-ObjectType-Operation, "REST URL Params"
("post_NetScaler-Vserver-Add","/lbvserver");

workflow-item.script:
//Based on code and an idea from http://www.vcoflow.co.uk/


var objProperties;
    objProperties = new Properties();

	//#######################################################################
	//Login - Currently Unused
	objProperties.put("get_NetScaler-login", "/rest");

	//#######################################################################
	//Virtual Server Operations 
	objProperties.put("post_NetScaler-LBVserver-Add","/lbvserver");
	objProperties.put("delete_NetScaler-LBVserver-Delete","/lbvserver/{vserverName}");
	objProperties.put("put_NetScaler-LBVserver-Update","/lbvserver");
	objProperties.put("post_NetScaler-LBVserver-Unset","/lbvserver?action=unset");
	objProperties.put("post_NetScaler-LBVserver-Enable","/lbvserver?action=enable");
	objProperties.put("post_NetScaler-LBVserver-Disable","/lbvserver?action=disable");
	objProperties.put("post_NetScaler-LBVserver-Rename","/lbvserver?action=rename");
	objProperties.put("get_NetScaler-LBVserver-GetAll", "/lbvserver");
	objProperties.put("get_NetScaler-VServers-GetAll-Summary", "/lbvserver?view=summary");
	objProperties.put("get_NetScaler-LBVserver-Get","/lbvserver/{vserverName}");

	//Virtual Server Service and Service Group  Binding
	objProperties.put("put_NetScaler-VServer-Add-ServiceGroup","/lbvserver_servicegroup_binding");
	objProperties.put("delete_NetScaler-VServer-Delete-ServiceGroup","/lbvserver_servicegroup_binding/{vserverName}?args=servicegroupname:{serviceGroupName}");
	objProperties.put("get_NetScaler-VServer-Get-ServiceGroups","/lbvserver_servicegroup_binding/{vserverName}");
	objProperties.put("post_NetScaler-VServer-Add-Service","/lbvserver_service_binding");
	objProperties.put("delete_NetScaler-VServer-Delete-Service","/lbvserver_service_binding/{vserverName}?args=servicename:{serviceName}");
	objProperties.put("get_NetScaler-VServer-Get-Services","/lbvserver_service_binding/{vserverName}");

	//#######################################################################
	//SSL Certificate Stuff
	//-----------------------------------------------------------------------------------------------
	//Certificate Binding
	objProperties.put("post_NetScaler-SSLServer-Certificate-Binding-Add","/sslvserver_sslcertkey_binding");
	objProperties.put("delete_NetScaler-SSLServer-Certificate-Bind-Delete","/sslvserver_sslcertkey_binding/{vserverName}?args={certKeyName}");
	objProperties.put("get_NetScaler-SSLServer-Certificate-Binding-Get","/sslvserver_sslcertkey_binding/{vserverName}");

	//-----------------------------------------------------------------------------------------------
	//Certificate Mgmt
	objProperties.put("put_NetScaler-SSLCertificate-Register","/sslcertkey");
	objProperties.put("delete_NetScaler-SSLCertificate-Binding-Add","/sslcertkey/{certKeyName}");
	objProperties.put("put_NetScaler-SSLCertificate-Binding-Update","/sslcertkey");
	objProperties.put("post_NetScaler-SSLCertificate-Binding-Unset","/sslcertkey?action=unset");
	objProperties.put("post_NetScaler-SSLCertificate-Binding-Link","/sslcertkey?action=link");
	objProperties.put("post_NetScaler-SSLCertificate-Binding-Unlink","/sslcertkey?action=unlink");
	objProperties.put("post_NetScaler-SSLCertificate-Binding-Change","/sslcertkey?action=update");
	objProperties.put("get_NetScaler-SSLCertificate-Binding-GetAll","/sslcertkey");
	objProperties.put("get_NetScaler-SSLCertificate-Binding-GetAll-SummaryView","/sslcertkey?view=summary");
	objProperties.put("get_NetScaler-SSLCertificate-Binding-Get","/sslcertkey/{certKeyName}");


	//#######################################################################
	//Service Group Operations
	objProperties.put("post_NetScaler-ServiceGroup-Add","/servicegroup");
	objProperties.put("delete_NetScaler-ServiceGroup-Delete","/servicegroup/{serviceGroupName}?warning=yes");
	objProperties.put("put_NetScaler-ServiceGroup-Update","/servicegroup");
	objProperties.put("post_NetScaler-ServiceGroup-Unset","/servicegroup?action=unset");
	objProperties.put("post_NetScaler-ServiceGroup-Enable","/servicegroup?action=enable");
	objProperties.put("post_NetScaler-ServiceGroup-Disable","/servicegroup?action=disable");
	objProperties.put("post_NetScaler-ServiceGroup-Rename","/servicegroup?action=renamte");
	objProperties.put("get_NetScaler-ServiceGroup-GetAll","/servicegroup");
	objProperties.put("get_NetScaler-ServiceGroup-GetAll-Summary","/servicegroup?view=summary");
	objProperties.put("get_NetScaler-ServiceGroup-Get","/servicegroup/{serviceGroupName}");

	//#######################################################################
	//Service Group Binding
	objProperties.put("post_NetScaler-ServiceGroupMember-Add","/servicegroup_servicegroupmember_binding");
	objProperties.put("delete_NetScaler-ServiceGroupMember-Delete","/servicegroup_servicegroupmember_binding/{serviceGroupName}?warning=yes&args=servername={serverName},port:{serverPort}");
	objProperties.put("get_NetScaler-ServiceGroupMember-Get","/servicegroup_servicegroupmember_binding/{serviceGroupName}");

	//#######################################################################
	//Service Operations
	objProperties.put("post_NetScaler-Service-Add","/service");
	objProperties.put("delete_NetScaler-Service-Delete","/service/{serviceName}?warning=yes");
	objProperties.put("put_NetScaler-Service-Update","/service");
	objProperties.put("post_NetScaler-Service-Unset","/service?action=unset");
	objProperties.put("post_NetScaler-Service-Enable","/service?action=enable");
	objProperties.put("post_NetScaler-Service-Disable","/service?action=disable");
	objProperties.put("post_NetScaler-Service-Rename","/service?action=rename");
	objProperties.put("get_NetScaler-Service-GetAll","/service");
	objProperties.put("get_NetScaler-Service-GetAll-Summary","/service?view=summary");
	objProperties.put("get_NetScaler-Service-Get","/service/{serviceName}");

	//#######################################################################
	//Server Operations
	objProperties.put("post_NetScaler-Server-Add","/server");
	objProperties.put("delete_NetScaler-Server-Delete","/server/{serviceName}?warning=yes");
	objProperties.put("put_NetScaler-Server-Update","/server");
	objProperties.put("post_NetScaler-Server-Unset","/server?action=unset");
	objProperties.put("post_NetScaler-Server-Enable","/server?action=enable");
	objProperties.put("post_NetScaler-Server-Disable","/server?action=disable");
	objProperties.put("post_NetScaler-Server-Rename","/server?action=rename");
	objProperties.put("get_NetScaler-Server-GetAll","/server");
	objProperties.put("get_NetScaler-Server-GetAll-Summary","/server?view=summary");
	objProperties.put("get_NetScaler-Server-Get","/server/{serverName}");

	//#######################################################################
	//Content Switching Virtual Server Operations	
	objProperties.put("post_NetScaler-CSvserver-Add","/csvserver");
	objProperties.put("delete_NetScaler-CSvserver-Delete","/csvserver/{serviceName}?warning=yes");
	objProperties.put("put_NetScaler-CSvserver-Update","/csvserver");
	objProperties.put("post_NetScaler-CSvserver-Unset","/csvserver?action=unset");
	objProperties.put("post_NetScaler-CSvserver-Enable","/csvserver?action=enable");
	objProperties.put("post_NetScaler-CSvserver-Disable","/csvserver?action=disable");
	objProperties.put("post_NetScaler-CSvserver-Rename","/csvserver?action=rename");
	objProperties.put("get_NetScaler-CSvserver-GetAll","/csvserver");
	objProperties.put("get_NetScaler-CSvserver-GetAll-Summary","/csvserver?view=summary");
	objProperties.put("get_NetScaler-CSvserver-Get","/csvserver/{csvserverName}");

	//#######################################################################
	//Content Switching Virtual Server Policy Binding Operations
	objProperties.put("post_NetScaler-CSvserver-Add-CSPolicy","/csvserver_cspolicy_binding");
	objProperties.put("delete_NetScaler-VServer-Delete-CSPolicy","/csvserver_cspolicy_binding/{csvserverName}?args=policyname:{policyName}");
	objProperties.put("put_NetScaler-CSvserver-Update-CSPolicy","/csvserver_cspolicy_binding/{csvserverName}");
	objProperties.put("get_NetScaler-CSvserver-Get-CSPolicy","/csvserver_cspolicy_binding/{csvserverName}");

	//#######################################################################
	//Content Switch Policy Operations	
	objProperties.put("post_NetScaler-CSpolicy-Add","/cspolicy");
	objProperties.put("delete_NetScaler-CSpolicy-Delete","/cspolicy/{serviceName}?warning=yes");
	objProperties.put("put_NetScaler-CSpolicy-Update","/cspolicy");
	objProperties.put("post_NetScaler-CSpolicy-Unset","/cspolicy?action=unset");
	objProperties.put("post_NetScaler-CSpolicy-Enable","/cspolicy?action=enable");
	objProperties.put("post_NetScaler-CSpolicy-Disable","/cspolicy?action=disable");
	objProperties.put("post_NetScaler-CSpolicy-Rename","/cspolicy?action=rename");
	objProperties.put("get_NetScaler-CSpolicy-GetAll","/cspolicy");
	objProperties.put("get_NetScaler-CSpolicy-GetAll-Summary","/cspolicy?view=summary");
	objProperties.put("get_NetScaler-CSpolicy-Get","/cspolicy/{cspolicyName}");

var arrKeys;
    arrKeys = objProperties.keys;

 System.log("Starting Loop\n");
    for ( var i = 0; i < arrKeys.length; i++ )
    {
        var strKey;
            strKey = arrKeys[i];

        var arrKey = new Array();
            arrKey = strKey.split('_');

 		var strOperationName;
			strOperationName = arrKey[1];

        var strMethod;
            strMethod = arrKey[0].toUpperCase();

        var strTemplateURL;
            strTemplateURL = objProperties.get(strKey);

		var strTemplateURLSplit;
			strTemplateURLSplit = strTemplateURL.split('/');

		var strTemplateURLSplit2;
			strTemplateURLSplit2 = strTemplateURLSplit[1].split('?');


		var strContentType;
			strContentType = "application/vnd.com.citrix.netscaler."+strTemplateURLSplit2[0]+"+json";

        var objRESTOperation;
            objRESTOperation = new RESTOperation(strOperationName);
            objRESTOperation.method = strMethod;
            objRESTOperation.urlTemplate = strTemplateURL;
            objRESTOperation.defaultContentType = strContentType;
 			System.log("RESTOperation: "+ strOperationName);
			System.log("\tMethod: "+ strMethod);
			System.log("\turlTemplate: "+ strTemplateURL);
	        objRESTOperation = NetScalerHost.addOperation(objRESTOperation);
			RESTHostManager.updateHost(NetScalerHost);
    }
